document.addEventListener('DOMContentLoaded', () => {
  const slidesContainer = document.querySelector('.hero__slides');
  const slides = document.querySelectorAll('.hero__slide');
  const navButtons = document.querySelectorAll('.hero__navButton');
  const totalSlides = slides.length;
  let currentSlide = 0;
  let interval;
  let slideHeight = 600;

  // Функція для оновлення позиції слайдера
  const updateSlider = () => {
    if (window.innerWidth >= 980) {
      slideHeight = 600;
    } else if (window.innerWidth >= 640 && window.innerWidth < 980) {
      slideHeight = 500;
    } else {
      slideHeight = 215;
    }
    slidesContainer.style.transform = `translateY(-${currentSlide * slideHeight}px)`;
    updateActiveButton();
  };

  // Функція для оновлення активної кнопки навігації
  const updateActiveButton = () => {
    navButtons.forEach(button => button.classList.remove('active'));
    navButtons[currentSlide].classList.add('active');
  };

  // Обробник натискання на кнопки навігації
  navButtons.forEach(button => {
    button.addEventListener('click', () => {
      currentSlide = parseInt(button.getAttribute('data-slide'));
      updateSlider();
      resetInterval();
    });
  });

  // Функція для автоматичного перемикання слайдів

  // Функція для зупинки автоматичного перемикання
  const stopAutoSlide = () => {
    clearInterval(interval);
  };

  // Функція для перезапуску інтервалу після взаємодії користувача
  const resetInterval = () => {
    stopAutoSlide();
    startAutoSlide();
  };

  // Зупинка автопрокрутки при наведенні на слайдер
  const slider = document.querySelector('.hero');
  slider.addEventListener('mouseenter', stopAutoSlide);
  slider.addEventListener('mouseleave', startAutoSlide);

  // Ініціалізація слайдера
  updateSlider();
  startAutoSlide();
});

